mapper.sql:
SELECT /*+ INDEX_DESC(A LGT_CHANGE_HIS_IDX1)*/ ACT_CODE, COALESCE(${db.mainschema}.F_GET_COMMON_CD_NM('LGT_CHANGE_HIS', 'ACT_CODE', ACT_CODE), ACT_CODE) ACT_CODE_NM, TO_CHAR(CHG_DT, 'YYYY-MM-DD HH24:MI') CHG_DT, SERVICE_ACCOUNT, ${db.mainschema}.F_GET_CUSTOMER_INFO('SERVICE_NUMBER','LGT', SERVICE_ACCOUNT) SERVICE_NUMBER, COL, COALESCE(${db.mainschema}.F_GET_COMMON_CD_NM('LGT_CHANGE_HIS', 'COL', COL), COL) COL_NM, OLD_VAL, ${db.mainschema}.F_GET_CHANGE_HIS_VAL(COL, NEW_VAL1, '1') NEW_VAL1, ${db.mainschema}.F_GET_CHANGE_HIS_VAL(COL, NEW_VAL2, '2') NEW_VAL2, ${db.mainschema}.F_GET_ADMIN_INFO('ADMIN_NAME', ADMIN_ID) ADMIN_NM, ERR FROM ${db.mainschema}.LGT_CHANGE_HIS A WHERE CHG_DT > TO_DATE('20150101', 'YYYYMMDD')
AND (A.SERVICE_ACCOUNT = (SELECT ${db.mainschema}.F_GET_ACCOUNT('LGT', #{sword})) OR A.SERVICE_ACCOUNT = (SELECT ${db.mainschema}.F_GET_ACCOUNT('LGT', '010' || #{sword})))
AND A.SERVICE_ACCOUNT = #{sword}
AND ${@telecentro.common.service.CommonUtil@get_dollar_replace_query(sst)} = #{sword}
${@telecentro.common.service.CommonUtil@get_multi_select_query("ACT_CODE", act_code)}
${@telecentro.common.service.CommonUtil@get_section_query("CHG_DT", null, s_dt, e_dt, "Y", null)}
A.SERVICE_ACCOUNT = '99999999999999999999';

mapper.select:
SELECT fq.ACT_CODE, fq.ACT_CODE_NM, fq.CHG_DT, fq.SERVICE_ACCOUNT, fq.SERVICE_NUMBER, fq.COL, fq.COL_NM, fq.OLD_VAL, fq.NEW_VAL1, fq.NEW_VAL2, fq.ADMIN_NM, fq.ERR
FROM (
ORDER BY A.CHG_DT DESC, A.SERVICE_ACCOUNT DESC LIMIT #{endRowNum} OFFSET #{startRowNum}
) fq
