mapper.sql:
SELECT /*+ INDEX_DESC(A SMS_DATE_IDX1) */ SERVICE_ACCOUNT, TO_CHAR(SEND_DATE, 'YYYY-MM-DD HH24:MI') SEND_DATE, SMS_MSG, CALLING_NUMBER, CALLED_NUMBER, RET_CD, COALESCE(RET_MSG, ${db.mainschema}.F_GET_COMMON_CD_NM('SMS', 'RET_CD', RET_CD)) RET_MSG, COALESCE(${db.mainschema}.F_GET_ADMIN_INFO('ADMIN_NAME', ADMIN_ID), ADMIN_ID) ADMIN_NM, SMS_GUBUN, ${db.mainschema}.F_GET_COMMON_CD_NM('SMS', 'SMS_GUBUN', SMS_GUBUN) SMS_GUBUN_NM, TELCO FROM ${db.mainschema}.SMS A WHERE SEND_DATE > TO_DATE('20150101', 'YYYYMMDD')
AND (A.SERVICE_ACCOUNT = (SELECT ${db.mainschema}.F_GET_ACCOUNT('ALL', #{sword})) OR A.SERVICE_ACCOUNT = (SELECT ${db.mainschema}.F_GET_ACCOUNT('ALL', '010' || #{sword})))
AND A.SERVICE_ACCOUNT = #{sword}
AND ${@telecentro.common.service.CommonUtil@get_dollar_replace_query(sst)} = #{sword}
${@telecentro.common.service.CommonUtil@get_section_query("SEND_DATE", null, s_dt, e_dt, "Y", null)}
A.SERVICE_ACCOUNT = '999999999999999999999';
AND SMS_GUBUN = #{sms_gubun}

mapper.select:
SELECT fq.SERVICE_ACCOUNT, fq.SEND_DATE, fq.SMS_MSG, fq.CALLING_NUMBER, fq.CALLED_NUMBER, fq.RET_CD, fq.RET_MSG, fq.ADMIN_NM, fq.SMS_GUBUN, fq.SMS_GUBUN_NM, fq.TELCO
FROM (
ORDER BY A.SEND_DATE DESC, A.SERVICE_ACCOUNT DESC, A.TELCO DESC LIMIT #{endRowNum} OFFSET #{startRowNum}
) fq
