mapper.select:
SELECT fq.SKT_AGENT_ID, fq.SKT_SALE_ID, fq.SKT_PAYAGENT_ID, fq.BL_ACNT_NUM, fq.SERVICE_ACCOUNT, fq.PAYMENT_SN, fq.SERVICE_NUMBER, fq.NO_MASK_SERVICE_NUMBER, fq.CUST_NAME, fq.OPEN_DATE, fq.CUST_PAY_DT, fq.TRANSF_DT, fq.PAY_OP_DT, fq.SETTL_WAY_CD, fq.PAY_MTHD_CD, fq.BILL_TYPE_CD, fq.BANK_CD, fq.BANK_NAME, fq.BANK_ACCOUNT, fq.CARD_OK_NO, fq.PAY_TYPE, fq.BILL_DT, fq.BILL_DT_MONTH, fq.CHARGE_ITEM_CD, fq.CHARGE_ITEM_CD_NM, fq.PAY_AMOUNT, fq.PAY_VIR_BANK, fq.PAY_VIR_ACCOUNT, fq.BILL_GUBUN, fq.AGENT_ID, fq.RECORD_DATE, fq.FILE_NAME, fq.ERR
FROM (
ORDER BY A.SERVICE_ACCOUNT DESC, A.CUST_PAY_DT DESC LIMIT #{endRowNum} OFFSET #{startRowNum}
) fq
