mapper.sql:
SELECT /*+ index_desc(a LGT_CHARGE_IDX3) */ A.BAN, A.NCN, A.SERVICE_ACCOUNT, A.SERVICE_TYPE, ${db.mainschema}.F_GET_COMMON_CD_NM('CUSTOMER_LGT', 'SERVICE_TYPE', A.SERVICE_TYPE) SERVICE_TYPE_NM, ${db.mainschema}.F_GET_CUSTOMER_INFO('SERVICE_NUMBER', 'LGT', A.SERVICE_ACCOUNT) SERVICE_NUMBER, TO_CHAR(BILL_TRGT_YYMM, 'YYYY-MM') BILL_TRGT_YYMM, TO_CHAR(PYM_DDAY_DT, 'YYYY-MM-DD') PYM_DDAY_DT, TO_CHAR(RFLCT_DT, 'YYYY-MM-DD') RFLCT_DT, TO_CHAR(RCPMNY_DT, 'YYYY-MM-DD') RCPMNY_DT, PYM_CD, ${db.mainschema}.F_GET_COMMON_CD_NM('LGT_CHARGE', 'PYM_CD', PYM_CD) PYM_CD_NM, BLITEM_CD, BLITEM_NM, PYM_MTHD_CD, PYM_MTHD_NM, PYM_MTHD_DETL_CD, PYM_MTHD_DETL_NM, ADJMT_RSN_CD, ADJMT_RSN_NM, BLNC_RFLCT_KD_CD, ${db.mainschema}.F_GET_COMMON_CD_NM('LGT_CHARGE', 'BLNC_RFLCT_KD_CD', BLNC_RFLCT_KD_CD) BLNC_RFLCT_KD_CD_NM, TRUNC(PYM_AMT, 2) PYM_AMT, TRUNC(VAT_AMT, 2) VAT_AMT, TRUNC(PYM_AMT + VAT_AMT, 2) TOT_AMT, AGENT_ID, ${db.mainschema}.F_GET_AGENT_INFO('AGENT_NAME', AGENT_ID) AGENT_NM
FROM ${db.mainschema}.LGT_CHARGE A WHERE BILL_TRGT_YYMM > to_date('2020-01-01', 'yyyy-mm-dd') AND PYM_CD IN ('INV', 'ADJR', 'ADJ')
AND SERVICE_ACCOUNT = #{sword}
AND (SERVICE_ACCOUNT = ${db.mainschema}.F_GET_ACCOUNT('LGT', #{sword}) OR SERVICE_ACCOUNT = ${db.mainschema}.F_GET_ACCOUNT('LGT', '010' || #{sword}))
AND ${@telecentro.common.service.CommonUtil@get_dollar_replace_query(sst)} = #{sword}
AND A.SERVICE_TYPE IS NULL
AND A.SERVICE_TYPE = #{service_type}
AND BILL_TRGT_YYMM >= TO_DATE(#{s_dt} || '-01', 'YYYY-MM-DD') AND BILL_TRGT_YYMM <= TO_DATE(#{e_dt} || '-01', 'YYYY-MM-DD')
AND A.SERVICE_ACCOUNT = '999999999999999999999'

mapper.select:
SELECT fq.BAN, fq.NCN, fq.SERVICE_ACCOUNT, fq.SERVICE_TYPE, fq.SERVICE_TYPE_NM, fq.SERVICE_NUMBER, fq.BILL_TRGT_YYMM, fq.PYM_DDAY_DT, fq.RFLCT_DT, fq.RCPMNY_DT, fq.PYM_CD, fq.PYM_CD_NM, fq.BLITEM_CD, fq.BLITEM_NM, fq.PYM_MTHD_CD, fq.PYM_MTHD_NM, fq.PYM_MTHD_DETL_CD, fq.PYM_MTHD_DETL_NM, fq.ADJMT_RSN_CD, fq.ADJMT_RSN_NM, fq.BLNC_RFLCT_KD_CD, fq.BLNC_RFLCT_KD_CD_NM, fq.PYM_AMT, fq.VAT_AMT, fq.TOT_AMT, fq.AGENT_ID, fq.AGENT_NM
FROM (
ORDER BY A.BILL_TRGT_YYMM DESC, A.SERVICE_ACCOUNT DESC LIMIT #{endRowNum} OFFSET #{startRowNum}
) fq
