mapper.sql:
SELECT /*+ INDEX_DESC(A SKT_CMS_FEE_IDX1) */ SK_AGENT_ID, TO_CHAR(CMS_REG_DT,'YYYY/MM/DD') CMS_REG_DT, SERVICE_ACCOUNT, SERVICE_NUMBER, BL_ACNT_NUM, CUST_FRST_NM, INCOME_TYPE_NM, BANK_CARD_CD, BANK_CARD_NM, PAY_AMOUNT, PAY_WILL_AMOUNT, INCOM_OK_NM, OPEN_SK_AGENT_ID, SK_ERR_MSG, RECORD_DATE, FILE_NAME, ERR FROM SKT_CMS_FEE A WHERE CMS_REG_DT > TO_DATE('20230101', 'YYYYMMDD')
AND (A.SERVICE_ACCOUNT = (SELECT F_GET_ACCOUNT('SKT', #{sword})) OR A.SERVICE_ACCOUNT = (SELECT F_GET_ACCOUNT('SKT', '010' || #{sword})))
AND A.SERVICE_ACCOUNT = #{sword}
AND ${@telecentro.common.service.CommonUtil@get_dollar_replace_query(sst)} = #{sword}
${@telecentro.common.service.CommonUtil@get_section_query("CMS_REG_DT", null, s_dt, e_dt, "Y", null)}

mapper.select:
SELECT fq.SK_AGENT_ID, fq.CMS_REG_DT, fq.SERVICE_ACCOUNT, fq.SERVICE_NUMBER, fq.BL_ACNT_NUM, fq.CUST_FRST_NM, fq.INCOME_TYPE_NM, fq.BANK_CARD_CD, fq.BANK_CARD_NM, fq.PAY_AMOUNT, fq.PAY_WILL_AMOUNT, fq.INCOM_OK_NM, fq.OPEN_SK_AGENT_ID, fq.SK_ERR_MSG, fq.RECORD_DATE, fq.FILE_NAME, fq.ERR
FROM (
ORDER BY A.CMS_REG_DT DESC, A.SERVICE_ACCOUNT DESC LIMIT #{endRowNum} OFFSET #{startRowNum}
) fq
