mapper.sql:
SELECT SKT_AGENT_ID, SKT_SALE_ID, SKT_PAYAGENT_ID, BL_ACNT_NUM, SERVICE_ACCOUNT, PAYMENT_SN, SERVICE_NUMBER, CUST_NAME, OPEN_DATE, TO_CHAR(CUST_PAY_DT,'YYYY-MM-DD') CUST_PAY_DT, TRANSF_DT, TO_CHAR(PAY_OP_DT,'YYYY-MM-DD') PAY_OP_DT, SETTL_WAY_CD, PAY_MTHD_CD, BILL_TYPE_CD, BANK_CD, BANK_NAME, CARD_OK_NO, PAY_TYPE, TO_CHAR(BILL_DT, 'YYYY-MM') BILL_DT, CHARGE_ITEM_CD, F_GET_COMMON_CD_NM('SKT_INV', 'CHARGE_ITEM_CD', CHARGE_ITEM_CD) CHARGE_ITEM_CD_NM, PAY_AMOUNT, PAY_VIR_BANK, PAY_VIR_ACCOUNT, BILL_GUBUN, AGENT_ID, F_GET_AGENT_INFO('AGENT_NAME',AGENT_ID) AGENT_NM, RECORD_DATE, FILE_NAME, ERR FROM SKT_PAY A WHERE CUST_PAY_DT > to_date('2018-09-01', 'yyyy-mm-dd')
AND CUST_PAY_DT >= TO_DATE(#{s_dt} || '-01', 'YYYY-MM-DD')
AND CUST_PAY_DT < TO_DATE(#{e_dt} || '-01', 'YYYY-MM-DD') + INTERVAL '1 month'
AND BILL_DT >= TO_DATE(#{s_b_month} || '-01', 'YYYY-MM-DD')
AND BILL_DT < TO_DATE(#{e_b_month} || '-01', 'YYYY-MM-DD') + INTERVAL '1 month'
AND SERVICE_ACCOUNT = #{sword}
AND (SERVICE_ACCOUNT = F_GET_ACCOUNT('SKT', #{sword}) OR SERVICE_ACCOUNT = F_GET_ACCOUNT('SKT', '010' || #{sword}))
AND BL_ACNT_NUM = #{sword}
AND ${@telecentro.common.service.CommonUtil@get_dollar_replace_query(sst)} = #{sword}

mapper.select:
SELECT fq.SKT_AGENT_ID, fq.SKT_SALE_ID, fq.SKT_PAYAGENT_ID, fq.BL_ACNT_NUM, fq.SERVICE_ACCOUNT, fq.PAYMENT_SN, fq.SERVICE_NUMBER, fq.CUST_NAME, fq.OPEN_DATE, fq.CUST_PAY_DT, fq.TRANSF_DT, fq.PAY_OP_DT, fq.SETTL_WAY_CD, fq.PAY_MTHD_CD, fq.BILL_TYPE_CD, fq.BANK_CD, fq.BANK_NAME, fq.CARD_OK_NO, fq.PAY_TYPE, fq.BILL_DT, fq.CHARGE_ITEM_CD, fq.CHARGE_ITEM_CD_NM, fq.PAY_AMOUNT, fq.PAY_VIR_BANK, fq.PAY_VIR_ACCOUNT, fq.BILL_GUBUN, fq.AGENT_ID, fq.AGENT_NM, fq.RECORD_DATE, fq.FILE_NAME, fq.ERR
FROM (
ORDER BY A.CUST_PAY_DT DESC, A.AGENT_ID DESC
ORDER BY A.BILL_DT DESC
LIMIT #{endRowNum} OFFSET #{startRowNum}
) fq
