mapper.sql:
SELECT TELCO, PROD_CD, SERVICE_TYPE, (CASE WHEN SERVICE_TYPE = 'VAS' THEN '부가서비스' WHEN SERVICE_TYPE = 'VA' THEN '부가요금제' ELSE SERVICE_TYPE END) SERVICE_TYPE_NM, SERVICE_NAME, BASIC_FEE, TO_CHAR(RECORD_DATE, 'YYYY-MM-DD HH24:MI') RECORD_DATE, ${db.mainschema}.F_GET_ADMIN_INFO('ADMIN_NAME', ADMIN_ID) ADMIN_NM, REMARK FROM ${db.mainschema}.CODE_SERVICE_INFO A WHERE SERVICE_TYPE LIKE 'VA%'
AND (PROD_CD LIKE '%' || #{sword} || '%' OR SERVICE_NAME LIKE '%' || #{sword} || '%')
AND ${@telecentro.common.service.CommonUtil@get_dollar_replace_query(sst)} = #{sword}
AND SERVICE_TYPE = #{service_type}
AND TELCO = #{telco}

mapper.select:
SELECT fq.TELCO, fq.PROD_CD, fq.SERVICE_TYPE, fq.SERVICE_TYPE_NM, fq.SERVICE_NAME, fq.BASIC_FEE, fq.RECORD_DATE, fq.ADMIN_NM, fq.REMARK
FROM (
ORDER BY A.RECORD_DATE DESC, A.PROD_CD LIMIT #{endRowNum} OFFSET #{startRowNum}
) fq
